import { IWedaRequestParams } from '@cloudbase/cloud-api';
/**
 * 微搭后端请求函数入参
 */
export interface IRequestParams extends Partial<Omit<IWedaRequestParams, 'uid' | 'source' | 'envType' | 'envId' | 'credentials'>> {
    /** 方法名称 */
    action: string;
    /** 云API参数 */
    data?: Record<string, any>;
}
/**
 * 用户来源信息
 */
export interface IUserSource {
    /** 用户ID */
    uid: string;
    /** uid 类型, 1.tcb 2.微信 3.企业微信 4.weda内部 */
    source: 1 | 2 | 3 | 4;
}
/**
 * 微搭用户信息
 * doc: https://tcloud-dev.oa.com/document/product/1505/53371?!preview&preview_docmenu=1&lang=cn&!document=1#WedaUser
 */
export interface IWedaUser {
    Uin: string;
    Uuid: string;
    EnvId: string;
    Source: 1 | 2;
    InternalUserType: 0 | 1;
    Type: 0 | 1 | 2;
    RelatedRoles: Array<IWedaRole>;
}
/**
 * 微搭角色信息
 */
export interface IWedaRole {
    RoleDesc: string;
    RoleIdentity: string;
    Name: string;
    Id: string;
}
