/**
 * 等待并阻塞进程
 * @param {number} ts 等待时间，单位毫秒
 * @returns {Promise<boolean>}
 */
function sleep(ts) {
  if (ts < 0) {
    throw new Error('function wait() 函数的 ts 参数不能小于 0');
  }
  if (ts === 0) {
    return Promise.resolve(true);
  }
  return new Promise((resolve) => {
    setTimeout(() => {
      resolve(true);
    }, ts);
  });
}

module.exports = { sleep };
