import { fetchWeda } from './remote-api';
/**
 * 调用数据源方法的参数
 */
export interface ICallDataSourceParams {
    /**
     * 数据源名称
     *  仅当 mode 为 ROUTE_NAMES.datasource (数据源) 有意义
     */
    name: string;
    /**
     * 方法名称
     *  仅当 mode 为 ROUTE_NAMES.datasource(数据源) 或 ROUTE_NAMES.common(公共模块) 有意义
     */
    methodName: string;
    /**
     * 方法参数
     *  各个模块均需要参数, 参数根据调用的方法而异, 部分方法不需要参数
     */
    params?: any;
    /** 环境类型 */
    envType?: 'prod' | 'pre';
}
/**
 * 调用数据源
 * @deprecated 请使用 callModel / callConnector 替代
 */
export declare function callDataSource(params: ICallDataSourceParams): Promise<any>;
/**
 * 调用模型数据源
 */
export declare function callModel(params: ICallDataSourceParams): Promise<any>;
/**
 * 调用连接器
 */
export declare const callConnector: typeof callDataSource;
/**
 * 调用数据源公共方法
 */
export declare function callCommonService(params: Omit<ICallDataSourceParams, 'name'>): Promise<any>;
/** 调用流程 */
export declare const callWorkflow: typeof fetchWeda;
/** 调用微搭API */
export declare const callWedaApi: typeof fetchWeda;
/**
 * 调用微信 API
 */
export declare function callWxOpenApi(params: any): Promise<any>;
