export { PageModule, IPageModuleOptions } from './pagemodule'

/**
 * 全局单例的 PageModule 实例
 */
export const pageModule: PageModule = new PageModule()

/**
 * 初始化全局的单例 PageModule
 * @param moduleName - 模块名称
 * @param options    - 模块参数
 * @returns
 */
export function init(moduleName: string, options: IPageModuleOptions = {}): PageModule {
  return pageModule.init(moduleName, options)
}

