import { IPlatformDataSource, IHttpMethod } from '@cloudbase/cals';
export interface ICallHttpMethod {
    /** 是否直接使用原有实现 */
    useLegacy?: boolean;
    /** 方法名称 */
    methodName: string;
    /** 数据源标识 */
    dataSourceName: string;
    /** 参数 */
    params?: Record<string, unknown>;
    /** 数据源配置信息 */
    dataSource: IPlatformDataSource;
    /** 方法的配置信息 */
    methodCfg: IHttpMethod;
    /**
     * 是否为测试模式
     */
    isTestMode?: boolean;
}
