type SystemMessage = {
    role: 'system';
    content: string;
};
type UserMessage = {
    role: 'user';
    content: string;
};
type AssistantMessage = {
    role: 'assistant';
    content?: string;
    tool_calls?: Array<{
        id: string;
        type: 'web_search' | 'retrieval' | 'function';
        function?: {
            name: string;
            auguments: string;
        };
    }>;
};
type ToolMessage = {
    role: 'tool';
    content: string;
    tool_call_id: string;
};
type ZhiPuMessage = SystemMessage | UserMessage | AssistantMessage | ToolMessage;
export type ZhiPuInputData = {
    model: string;
    messages: Array<ZhiPuMessage>;
    request_id?: string;
    do_sample?: boolean;
    temperature?: number;
    top_p?: number;
    max_tokens?: number;
    stop?: Array<string>;
    tools?: Array<{
        type: 'web_search' | 'retrieval' | 'function';
        function: {
            name: string;
            description: string;
            parameters: object;
        };
        retrieval: {
            knowledge_id: string;
            prompt_template?: string;
            parameters: object;
        };
        web_search: {
            enable?: boolean;
            search_query?: string;
            search_result?: boolean;
        };
    }>;
    tool_choice?: 'auto';
    user_id?: string;
};
export type ZhiPuGenerateTextOutput = {
    choices?: Array<{
        message?: {
            content?: string;
        };
    }>;
};
export type ZhiPuStreamTextOutput = {
    choices?: Array<{
        delta?: {
            content?: string;
        };
    }>;
};
export {};
