type ArkMessage = {
  role: 'user' | 'system' | 'assistant'
  content:
  | string
  | Array<{
    type: 'image_url'
    image_url: {
      url: string
      image_bytes: string
      detail: string
    }
  }>
}

// 文档描述模糊，暂只提供基础定义
export type ArkInputData = {
  model: string
  messages: Array<ArkMessage>
  max_tokens?: number
  stop?: string | Array<string>
  frequency_penalty?: number
  temperature?: number
  top_p?: number
  logprobs?: boolean
  top_logprobs?: number
  logit_bias?: Record<string, number>
}

// todo: more precise
export type ArkGenerateTextOutput = {
  choices?: Array<{
    message?: { content?: string }
  }>
}

// todo: more precise
export type ArkStreamTextOutput = {
  choices?: Array<{
    message?: { content?: string }
  }>
}
